(function (app) {
    'use strict';

    app.controller('UserController', function ($http, $window) {
        var self = this;

        self.user = {
            name: '',
            password: ''
        };

        self.message = '';

        self.submit = function () {
            $http
                .post('/api/users/login', self.user)
                .success(function (data, status, headers, config) {
                    $window.sessionStorage.token = data.data.token;
                    if(!status == 200){
                        return false;
                    } else {
                        self.message = 'Welcome ' + self.user.name;
                    }

                })
                .error(function (data, status, headers, config) {
                    //Erase the token if the user fails to log in
                    delete $window.sessionStorage.token;

                    //handle login errors here
                    self.message = 'Error: Invalid user or password';
                });
        }

        self.getUsers = function () {
            $http
                .get('/api/users', {
                    headers: {'x-access-token': 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..C5uNSi-M5mHY55B-Ea9RYUZr2vAwGdj_Eic4OnCo4-A'}
                })
                .success(function (data, status, headers, config) {
                    self.message = data;
                });

        }
    });



})(angular.module('geofeelings'));

